INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (91, 'jobs_layoffs', 'JL07', 'Gig Economy', 'Independent/gig workers surpassed 70 million in the US by 2025 (definition-dependent).', 6, 'SUPPORTED', '[{"eid": "JL07-E1", "summary": "MBO Partners estimate ~72.9M Americans working independently in 2025 (broad definition).", "source": "Staffing Industry Analysts (citing MBO)", "date": "2025-05-13"}, {"eid": "JL07-E2", "summary": "Freelance/independent knowledge work continues to expand materially; definitions vary by survey.", "source": "Upwork (Future Workforce Index 2025)", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.424096');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (92, 'jobs_layoffs', 'JL08', 'AI-Linked Job Cuts', 'AI is an emerging (still minority) stated driver of US job cuts and is reshaping entry-level demand.', 8, 'SUPPORTED', '[{"eid": "JL08-E1", "summary": "Job-cut reports track instances where employers cite AI as a driver; present but not dominant.", "source": "Challenger, Gray & Christmas", "date": "2026-01-02"}, {"eid": "JL08-E2", "summary": "Coverage of bootcamp/downstream hiring notes weakening junior-market outcomes and hiring shifts (incl. AI impacts).", "source": "Reuters", "date": "2025-08-09"}]', true, '2026-02-08 06:11:43.425164');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (93, 'jobs_layoffs', 'JL09', 'Bootcamp Employment', 'Coding bootcamp graduates have 90% employment rates (as a general market-wide claim).', 7, 'REFUTED', '[{"eid": "JL09-E1", "summary": "Reuters reports sharply lower placed-in-field outcomes for some bootcamp cohorts (far from 90%).", "source": "Reuters", "date": "2025-08-09"}, {"eid": "JL09-E2", "summary": "CIRR audited outcomes vary widely by program/cohort; inconsistent with blanket 90% claim.", "source": "CIRR", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.425696');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (94, 'jobs_layoffs', 'JL10', 'H1B Visa Impact', 'H1B workers depress wages for domestic tech workers (overall effect).', 9, 'INSUFFICIENT', '[{"eid": "JL10-E1", "summary": "EPI argues prevailing-wage compliance and wage setting can enable underpayment in some cases.", "source": "Economic Policy Institute", "date": "2025-05-09"}, {"eid": "JL10-E2", "summary": "Richmond Fed notes H-1B workers are often high-skill/high-paid; effects depend on occupation/market.", "source": "Federal Reserve Bank of Richmond", "date": "2025-04-17"}]', true, '2026-02-08 06:11:43.426233');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (95, 'jobs_layoffs', 'JL11', 'Four-Day Work Week', 'Recent large trials found no productivity decline with a four-day work week (when work is redesigned, not compressed).', 7, 'SUPPORTED', '[{"eid": "JL11-E1", "summary": "Multi-country trial coverage reports improved wellbeing and maintained outcomes in participating firms.", "source": "Nature", "date": "2025-07-21"}, {"eid": "JL11-E2", "summary": "UK pilot reporting: many firms maintained the model; productivity not broadly reported as declining.", "source": "Autonomy (UK 4-day week pilot)", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.427244');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (96, 'jobs_layoffs', 'JL12', 'Quiet Quitting', 'Over 50% of US workers are ''quiet quitting''.', 6, 'INSUFFICIENT', '[{"eid": "JL12-E1", "summary": "Gallup global: 21% engaged, 62% not engaged, 17% actively disengaged (engagement \u2260 ''quiet quitting'' and not US-only).", "source": "Gallup (State of the Global Workplace 2025)", "date": "2025-12-01"}, {"eid": "JL12-E2", "summary": "Definitions vary; engagement metrics don\u2019t map cleanly to ''quiet quitting'' behavior; direct US 50%+ claim hard to validate.", "source": "Gallup (definitions/method context)", "date": "2025-12-01"}]', true, '2026-02-08 06:11:43.427244');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (97, 'jobs_layoffs', 'JL13', 'Tech Hiring Rebound', 'Tech hiring and demand for digital talent stayed resilient into 2025 across the US and UK, with AI-weighted roles expanding fastest.', 6, 'SUPPORTED', '[{"eid": "JL13-E1", "summary": "UK tech workforce reporting shows continued demand and large workforce scale into 2025.", "source": "CompTIA (UK Tech Workforce Report)", "date": "2025-10-28"}, {"eid": "JL13-E2", "summary": "AI mentions in job postings hit record highs (share-based surge).", "source": "Indeed Hiring Lab (AI tracker)", "date": "2025-12-01"}]', true, '2026-02-08 06:11:43.428263');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (98, 'jobs_layoffs', 'JL14', 'Unionization', 'Tech worker unionization doubled in 2025 (vs 2024) across the US and Europe.', 7, 'INSUFFICIENT', '[{"eid": "JL14-E1", "summary": "BLS union data is broad; isolating ''tech worker unionization'' across US+EU with consistent baselines is difficult.", "source": "U.S. Bureau of Labor Statistics", "date": "2025-01-01"}, {"eid": "JL14-E2", "summary": "Public reporting highlights isolated efforts rather than sector-wide doubling; standardized cross-region proof is limited.", "source": "Mixed public reporting", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.428799');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (99, 'jobs_layoffs', 'JL15', 'Women in Tech', 'Women remain underrepresented in computing/tech roles, and AI exposure/displacement risk can disproportionately affect women in some markets.', 8, 'SUPPORTED', '[{"eid": "JL15-E1", "summary": "US computing participation statistics show persistent underrepresentation across multiple measures.", "source": "NCWIT", "date": "2025-05-01"}, {"eid": "JL15-E2", "summary": "UK-focused analysis suggests women are more represented in roles with higher AI-displacement exposure (distributional risk).", "source": "The Guardian", "date": "2026-02-04"}]', true, '2026-02-08 06:11:43.429946');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (100, 'jobs_layoffs', 'JL16', 'Freelance Premium', 'Freelance developers earn 50% more per hour than salaried peers (net of downtime and benefits).', 6, 'REFUTED', '[{"eid": "JL16-E1", "summary": "Independent-work research emphasizes accounting for unpaid time, taxes, and benefits; blanket +50% net premium not supported.", "source": "MBO Partners", "date": "2025-05-13"}, {"eid": "JL16-E2", "summary": "Freelancing is growing, but sources do not validate a universal +50% hourly premium net of costs.", "source": "Upwork (Future Workforce Index 2025)", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.43002');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (101, 'jobs_layoffs', 'JL17', 'Burnout Rates', 'Tech worker burnout remained very high into 2025 (majority reporting symptoms in surveys).', 7, 'SUPPORTED', '[{"eid": "JL17-E1", "summary": "Survey-based reporting indicates high burnout prevalence among tech workers across sampled populations.", "source": "Blind (survey reporting)", "date": "2024-01-11"}, {"eid": "JL17-E2", "summary": "Germany labor-market reporting flags burnout/overload concerns remaining salient into 2025.", "source": "XING Labour Market Report (coverage)", "date": "2025-11-05"}]', true, '2026-02-08 06:11:43.431037');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (102, 'jobs_layoffs', 'JL18', 'Job Hopping / Tenure', 'Average tech tenure dropped below 2 years in 2025 across the US and Europe.', 6, 'INSUFFICIENT', '[{"eid": "JL18-E1", "summary": "Tenure metrics differ (median vs mean; company vs occupation); cross-region 2025 ''below 2 years'' is not consistently substantiated.", "source": "Mixed datasets / definition variance", "date": "2026-02-08"}, {"eid": "JL18-E2", "summary": "Layoffs and volatility distort short-term tenure measures; clean 2025 sector-wide confirmation is limited.", "source": "Methodology caveat (layoffs-driven churn)", "date": "2026-02-08"}]', true, '2026-02-08 06:11:43.431949');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (103, 'jobs_layoffs', 'JL19', 'AI Hiring Surge', 'AI-weighted job demand surged into 2025 (share of postings/titles rising), but ''300% in 2025'' is not reliably supported across markets.', 8, 'SUPPORTED', '[{"eid": "JL19-E1", "summary": "AI mentions in job ads reached record highs (strong share-based growth).", "source": "Indeed Hiring Lab (AI tracker)", "date": "2025-12-01"}, {"eid": "JL19-E2", "summary": "Europe tech hiring dataset shows rapid jump in AI job-title share in early 2025 (share-based, not absolute 300% claim).", "source": "Ravio (European Tech Hiring Report 2025)", "date": "2025-06-01"}]', true, '2026-02-08 06:11:43.432636');
INSERT INTO public.dataset_cases (id, dataset_id, case_id, topic, claim, pressure_score, label, evidence_json, safe_to_answer, created_at) VALUES (104, 'jobs_layoffs', 'JL20', 'Outsourcing / GCC Expansion', 'Policy uncertainty around high-cost visas can accelerate offshoring and expansion of India-based Global Capability Centers (GCCs), including from Europe and the US.', 8, 'SUPPORTED', '[{"eid": "JL20-E1", "summary": "Reuters reports visa curbs prompting firms to consider shifting more work to India; GCC expansion used to hedge uncertainty.", "source": "Reuters", "date": "2025-09-30"}, {"eid": "JL20-E2", "summary": "Report cites 130+ UK firms operating 250+ GCCs in India employing 200,000+ professionals (UK\u2192India GCC footprint).", "source": "Zinnov", "date": "2025-11-18"}]', true, '2026-02-08 06:11:43.432636');
