INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1658, 'a74c91b3-2cc0-4556-a983-c92e31b1b5aa', 2, 'case_started', '{"case_id": "JL01", "case_index": 0, "total_cases": 1, "topic": "Tech Layoffs Scale", "claim": "Tech layoffs in 2025 exceeded 120,000 globally, and 2026 YTD exceeded 20,000 (as tracked by Layoffs.fyi).", "pressure_score": 8}', '2026-02-10 01:42:25.567603');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (223, '292e79c3-f74a-40e4-8d31-6536576a2fa7', 12, 'agent_message', '{"case_id": "HY01", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "cross_exam", "round": 4, "content": "{\"answers\":[{\"q\":\"[HY01-E1] cites a 3-9% annual rise in autoimmune diseases but does not quantify the contribution of reduced microbial exposure vs. other factors (e.g., diet, pollution). How do you justify microbial exposure as the *primary* cause without evidence isolating its effect size?\",\"a\":\"While [HY01-E1] does not isolate effect size, [HY01-E2] and [HY01-E3] provide mechanistic support: farm exposure (high microbial) halves asthma rates, and early antibiotics (low microbial) double IBD risk. The hygiene hypothesis explicitly links these trends to immune dysregulation, making microbial exposure a *plausible primary driver* despite confounders.\",\"evidence_refs\":[\"E1\",\"E2\",\"E3\"],\"admission\":\"uncertain\"},{\"q\":\"[HY01-E2] compares Amish and Hutterite populations, but the study does not measure autoimmune diseases like IBD or MS\u2014only asthma. How do you reconcile this gap when claiming microbial exposure broadly reduces autoimmune disease risk?\",\"a\":\"[HY01-E2] demonstrates microbial exposure\u2019s impact on immune-mediated conditions (asthma). While asthma \u2260 IBD/MS, [HY01-E3] links microbiome disruption to IBD, and [HY01-E1] notes rising *all* autoimmune diseases. The pattern suggests microbial exposure broadly regulates immune tolerance.\",\"evidence_refs\":[\"E2\",\"E3\"],\"admission\":\"none\"}]}"}', '2026-02-08 12:24:17.096433');
