INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (278, '02a37dbf-00a5-4756-82ef-6c615ec1141b', 5, 'agent_message', '{"case_id": "HY09", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"SUPPORTED\",\"evidence_used\":[\"HY09-E1\",\"HY09-E2\",\"HY09-E3\"],\"key_points\":[\"Cumulative cognitive deficits from 6-hr sleep restriction match 48 hours of total deprivation on objective tasks (PVT, working memory) [HY09-E1]\",\"Neuroimaging reveals chronic 6-hr sleepers exhibit reduced prefrontal cortex activation and increased default mode interference, mirroring total deprivation effects [HY09-E2]\",\"Self-reported sleepiness fails to reflect actual impairment, masking the severity of cognitive decline [HY09-E3]\"],\"uncertainties\":[\"Long-term adaptation effects beyond 14 days remain untested\",\"Individual variability in cognitive resilience to sleep restriction is not fully quantified\"],\"what_would_change_my_mind\":[\"Evidence showing 6-hr sleepers develop compensatory neural mechanisms over time\",\"Studies demonstrating no equivalence to total deprivation after extended (>14-day) restriction\"]}"}', '2026-02-08 14:32:31.68678');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (279, '02a37dbf-00a5-4756-82ef-6c615ec1141b', 6, 'agent_message', '{"case_id": "HY09", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"INSUFFICIENT\",\"evidence_used\":[\"E1\",\"E3\"],\"key_points\":[\"Cognitive deficits in [HY09-E1] are task-specific (PVT/working memory) and may not generalize to all cognitive domains, limiting equivalence to total deprivation''s broader impairment.\",\"[HY09-E3] shows self-reported sleepiness plateaus after day 3, suggesting subjective adaptation that could mitigate *perceived* impairment despite objective deficits, complicating direct equivalence claims.\"],\"uncertainties\":[\"Whether cumulative deficits in [HY09-E1] reflect *sustained* impairment or temporary performance lags that stabilize over time.\",\"If neuroimaging changes in [HY09-E2] correlate with functional cognitive decline or are compensatory adaptations.\"],\"what_would_change_my_mind\":[\"Longitudinal data showing 6-hr sleepers exhibit *progressive* cognitive decline beyond 14 days, not just task-specific deficits.\",\"Evidence that default mode network interference in [HY09-E2] directly impairs real-world decision-making equivalently to total deprivation.\"]}"}', '2026-02-08 14:32:31.706885');
