INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (281, '02a37dbf-00a5-4756-82ef-6c615ec1141b', 8, 'case_phase_started', '{"case_id": "HY09", "model_key": "mistral/mistral-large-latest", "phase": "cross_exam"}', '2026-02-08 14:32:31.73181');
