INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (856, '7cf84490-10f1-4dd1-877d-08bed10a685f', 23, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "dispute", "round": 3, "content": "{\"answers\":[{\"q\":\"Does [JL15-E2]''s UK data demonstrate that women''s higher AI-displacement exposure is attributable to gender (not role/industry confounds), or does it merely reflect their overrepresentation in roles with inherent displacement risk (e.g., clerical vs. technical)?\",\"a\":\"[JL15-E2] does not demonstrate gender-attributable risk. The evidence lacks controls for role/industry confounds, leaving the cause ambiguous\u2014women may be overrepresented in inherently high-risk roles, not disproportionately affected *because* of gender.\",\"evidence_refs\":[\"E2\"],\"admission\":\"insufficient\"}]}"}', '2026-02-09 18:51:23.684826');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (973, '37fddbe4-1afc-4f7a-b82b-47b1e9c6b1b6', 2, 'case_started', '{"case_id": "JL01", "case_index": 0, "total_cases": 1, "topic": "Tech Layoffs Scale", "claim": "Tech layoffs in 2025 exceeded 120,000 globally, and 2026 YTD exceeded 20,000 (as tracked by Layoffs.fyi).", "pressure_score": 8}', '2026-02-09 19:19:05.332299');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (838, '7cf84490-10f1-4dd1-877d-08bed10a685f', 5, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"SUPPORTED\",\"evidence_used\":[\"JL15-E1\",\"JL15-E2\"],\"key_points\":[\"Persistent underrepresentation of women in computing/tech roles is documented in US statistics [JL15-E1], establishing a baseline disparity.\",\"Women in the UK are disproportionately concentrated in tech roles with higher AI-displacement risk [JL15-E2], suggesting gendered exposure to automation threats.\"],\"uncertainties\":[\"Causality between underrepresentation and AI risk is not fully established in the evidence.\",\"Geographic scope (US/UK) may limit generalizability to other markets.\"],\"what_would_change_my_mind\":[\"Evidence showing women are *not* overrepresented in high-AI-risk roles in other major markets.\",\"Data demonstrating parity in tech participation across all measures [JL15-E1].\"]}"}', '2026-02-09 18:50:40.24792');
