INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1580, 'f74d4005-37e8-4215-845b-0385287c411a', 2, 'case_started', '{"case_id": "JL10", "case_index": 0, "total_cases": 1, "topic": "H1B Visa Impact", "claim": "H1B workers depress wages for domestic tech workers (overall effect).", "pressure_score": 9}', '2026-02-10 01:16:30.072334');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1248, '9e13f2e7-d63e-4338-b57e-08fca152e521', 7, 'agent_message', '{"case_id": "AC01", "model_key": "mistral/mistral-large-latest", "role": "Skeptic", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"REFUTED\",\"evidence_used\":[\"AC01-E1\",\"AC01-E2\"],\"key_points\":[\"Meta-analysis of 53 RCTs ([AC01-E1]) demonstrates low-carb diets outperform grain-based diets in weight loss and metabolic health, directly contradicting the pyramid''s grain-centric claim.\",\"USDA''s 2011 MyPlate revision ([AC01-E2]) acknowledges the original pyramid''s grain-heavy recommendations lacked strong RCT support and were influenced by industry lobbying, undermining its scientific credibility.\"],\"uncertainties\":[\"Whether the USDA''s internal review ([AC01-E2]) fully accounted for all confounding variables in grain-heavy diet studies.\",\"Long-term adherence and health outcomes of low-carb vs. grain-based diets beyond the scope of the RCTs in [AC01-E1].\"],\"what_would_change_my_mind\":[\"Peer-reviewed RCTs showing grain-heavy diets produce superior metabolic outcomes compared to low-carb diets, with no industry influence.\",\"Evidence that the USDA''s 2011 revision ([AC01-E2]) was based on flawed methodology or omitted critical data.\"]}"}', '2026-02-09 20:14:05.980629');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1429, 'f176fba2-56fb-4446-a195-bb8c869b003f', 2, 'case_started', '{"case_id": "JL14", "case_index": 0, "total_cases": 1, "topic": "Unionization", "claim": "Tech worker unionization doubled in 2025 (vs 2024) across the US and Europe.", "pressure_score": 7}', '2026-02-09 23:21:18.316833');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1439, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 6, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Skeptic", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"INSUFFICIENT\",\"evidence_used\":[\"E1\",\"E2\"],\"key_points\":[\"E1 confirms underrepresentation in US computing roles, but lacks global scope or causation details.\",\"E2 suggests distributional AI risk in the UK, but does not quantify displacement or link to underrepresentation.\"],\"uncertainties\":[\"Is the UK trend (E2) generalizable to other markets? No cross-market comparison provided.\",\"Does underrepresentation (E1) directly correlate with AI exposure risk, or are other factors at play?\",\"Are the roles with higher AI exposure (E2) the same roles where women are underrepresented (E1)?\"],\"what_would_change_my_mind\":[\"Evidence showing consistent AI-displacement risk patterns across multiple markets (beyond UK).\",\"Data linking underrepresentation (E1) to specific roles with measurable AI exposure (E2).\",\"Longitudinal studies demonstrating causation between underrepresentation and displacement risk.\"]}"}', '2026-02-09 23:29:15.02096');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1440, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 7, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"INSUFFICIENT\",\"evidence_used\":[\"E1\",\"E2\"],\"key_points\":[\"Underrepresentation does not inherently prove systemic bias or future risk; [JL15-E1] shows participation gaps but lacks causal analysis of barriers or market dynamics.\",\"[JL15-E2] highlights distributional risk in the UK, but this is a localized snapshot\u2014no evidence confirms global or long-term trends.\",\"AI displacement exposure may reflect role distribution (e.g., women in clerical tech jobs), not gender-specific targeting; correlation \u2260 causation.\"],\"uncertainties\":[\"Are underrepresented women in tech *more* exposed to AI displacement than underrepresented men in the same roles?\",\"Does [JL15-E2] account for confounding factors (e.g., education, career stage, or industry sub-sector)?\"],\"what_would_change_my_mind\":[\"Cross-market data showing women in tech face *higher* displacement risk than men in *identical* roles.\",\"Evidence that underrepresentation is driven by exclusionary practices (not preferences, pipeline gaps, or cultural factors).\"]}"}', '2026-02-09 23:29:16.11428');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1441, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 8, 'case_phase_started', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "phase": "cross_exam"}', '2026-02-09 23:29:16.123632');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1442, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 9, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "cross_exam", "round": 1, "content": "{\"questions\":[{\"to\":\"Heretic\",\"q\":\"You argue [JL15-E1] lacks causal analysis of barriers, but the claim only requires establishing *underrepresentation* as a baseline. How does the absence of causality in [JL15-E1] justify dismissing the documented disparity itself?\",\"evidence_refs\":[\"JL15-E1\"]},{\"to\":\"Heretic\",\"q\":\"You note [JL15-E2] is a ''localized snapshot,'' but the claim specifies ''some markets'' (e.g., UK). What *additional* evidence\u2014beyond [JL15-E2]\u2014would be required to confirm AI-displacement risk is *not* gendered in the UK context?\",\"evidence_refs\":[\"JL15-E2\"]}]}"}', '2026-02-09 23:29:19.275465');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1443, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 10, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "cross_exam", "round": 2, "content": "{\"answers\":[{\"q\":\"You argue [JL15-E1] lacks causal analysis of barriers, but the claim only requires establishing *underrepresentation* as a baseline. How does the absence of causality in [JL15-E1] justify dismissing the documented disparity itself?\",\"a\":\"[JL15-E1] confirms underrepresentation, but the claim links it to *future* AI risk. Without causal analysis, we cannot assume underrepresentation *causes* disproportionate exposure\u2014it may reflect unrelated factors (e.g., role choices).\",\"evidence_refs\":[\"E1\"],\"admission\":\"none\"},{\"q\":\"You note [JL15-E2] is a ''localized snapshot,'' but the claim specifies ''some markets'' (e.g., UK). What *additional* evidence\u2014beyond [JL15-E2]\u2014would be required to confirm AI-displacement risk is *not* gendered in the UK context?\",\"a\":\"To disprove gendered risk in the UK, we\u2019d need: (1) role-level displacement data comparing women/men in *identical* jobs, and (2) controls for confounders (e.g., tenure, sub-sector). [JL15-E2] lacks both.\",\"evidence_refs\":[\"E2\"],\"admission\":\"insufficient\"}]}"}', '2026-02-09 23:29:23.944506');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1444, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 11, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "cross_exam", "round": 3, "content": "{\"questions\":[{\"to\":\"Orthodox\",\"q\":\"Does [JL15-E1] provide any data on whether the underrepresentation of women in tech is due to systemic barriers (e.g., hiring bias, workplace discrimination) rather than pipeline or preference differences? If not, how can we conclude the claim of ''disproportionate risk'' is causal?\",\"evidence_refs\":[\"E1\"]},{\"to\":\"Orthodox\",\"q\":\"[JL15-E2] shows UK women are in higher-AI-risk roles, but does it control for role type (e.g., clerical vs. technical) or career stage? Without this, how do we rule out that the risk is role-specific rather than gender-specific?\",\"evidence_refs\":[\"E2\"]}]}"}', '2026-02-09 23:29:27.204959');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1510, '4184f77a-d01b-4022-8872-9492120e21e2', 25, 'agent_message', '{"case_id": "JL01", "model_key": "mistral/mistral-large-latest", "role": "Judge", "phase": "judge", "round": 1, "content": "```toml\nverdict = \"SUPPORTED\"\nconfidence = 0.85\nevidence_used = [\"JL01-E1\", \"JL01-E2\"]\nreasoning = \"The evidence ([JL01-E1], [JL01-E2]) directly confirms the claim''s thresholds (123,941 layoffs in 2025 exceeding 120,000 and 24,818 YTD in 2026 exceeding 20,000). While methodological limitations exist, no evidence in the pack disproves the raw totals or the claim''s framing.\"\n```"}', '2026-02-09 23:52:19.915943');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1253, '9e13f2e7-d63e-4338-b57e-08fca152e521', 12, 'agent_message', '{"case_id": "AC01", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "cross_exam", "round": 4, "content": "{\"answers\":[{\"q\":\"How do you reconcile the USDA''s 2011 MyPlate revision ([AC01-E2])\u2014which admits the original pyramid''s grain emphasis lacked RCT support\u2014with your claim that the pyramid merely needed ''refinement'' rather than fundamental correction?\",\"a\":\"The MyPlate revision ([AC01-E2]) acknowledged gaps in RCT support for the *degree* of grain emphasis, not the *absence* of grains'' role. It refined portion sizes and context (e.g., whole vs. refined grains), aligning with evolving evidence while preserving grains as a dietary foundation.\",\"evidence_refs\":[\"AC01-E2\"],\"admission\":\"none\"},{\"q\":\"Given the meta-analysis ([AC01-E1]) shows low-carb diets outperform grain-heavy diets in RCTs, what specific RCT evidence (not cited in the packets) supports your assertion that grains remain ''foundational'' for the general population?\",\"a\":\"INSUFFICIENT evidence in pack. The packets do not include RCTs comparing long-term health outcomes of grain-heavy vs. low-carb diets for the general population. My claim relies on grains'' historical consensus role, not direct counter-evidence.\",\"evidence_refs\":[],\"admission\":\"insufficient\"}]}"}', '2026-02-09 20:14:22.183723');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1430, 'f176fba2-56fb-4446-a195-bb8c869b003f', 3, 'case_phase_started', '{"case_id": "JL14", "model_key": "mistral/mistral-large-latest", "phase": "setup"}', '2026-02-09 23:21:18.822925');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1432, 'f176fba2-56fb-4446-a195-bb8c869b003f', 5, 'agent_message', '{"case_id": "JL14", "model_key": "mistral/mistral-large-latest", "role": "Heretic", "phase": "independent", "round": 1, "content": "{\"proposed_verdict\":\"INSUFFICIENT\",\"evidence_used\":[\"E1\",\"E2\"],\"key_points\":[\"No standardized baseline exists for ''tech worker unionization'' across US+EU, making a ''doubling'' claim unverifiable ([JL14-E1])\",\"Public reporting focuses on isolated cases, not sector-wide trends, lacking proof of systemic growth ([JL14-E2])\"],\"uncertainties\":[\"How ''tech worker'' is defined in cross-region data\",\"Whether reported efforts translate to measurable membership increases\"],\"what_would_change_my_mind\":[\"Consistent, regionally harmonized data showing tech-specific unionization rates\",\"Longitudinal evidence of sector-wide membership growth beyond anecdotal cases\"]}"}', '2026-02-09 23:21:22.999267');
INSERT INTO public.run_events (id, run_id, seq, event_type, payload_json, created_at) VALUES (1445, 'b3913f0d-21be-4fca-b4d0-135898cba9c4', 12, 'agent_message', '{"case_id": "JL15", "model_key": "mistral/mistral-large-latest", "role": "Orthodox", "phase": "cross_exam", "round": 4, "content": "{\"answers\":[{\"q\":\"Does [JL15-E1] provide any data on whether the underrepresentation of women in tech is due to systemic barriers (e.g., hiring bias, workplace discrimination) rather than pipeline or preference differences? If not, how can we conclude the claim of ''disproportionate risk'' is causal?\",\"a\":\"[JL15-E1] documents persistent underrepresentation but does NOT specify causes (e.g., systemic barriers vs. pipeline/preferences). The claim of ''disproportionate risk'' is correlational, not causal.\",\"evidence_refs\":[\"JL15-E1\"],\"admission\":\"uncertain\"},{\"q\":\"[JL15-E2] shows UK women are in higher-AI-risk roles, but does it control for role type (e.g., clerical vs. technical) or career stage? Without this, how do we rule out that the risk is role-specific rather than gender-specific?\",\"a\":\"[JL15-E2] indicates distributional risk but does NOT detail role-type or career-stage controls. The evidence suggests gendered exposure, but role-specific factors cannot be ruled out.\",\"evidence_refs\":[\"JL15-E2\"],\"admission\":\"uncertain\"}]}"}', '2026-02-09 23:29:31.121747');
