INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (483, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Skeptic', '{"proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"key_points":["E1 confirms underrepresentation in US computing roles, but lacks global scope or causation details.","E2 suggests distributional AI risk in the UK, but does not quantify displacement or link to underrepresentation."],"uncertainties":["Is the UK trend (E2) generalizable to other markets? No cross-market comparison provided.","Does underrepresentation (E1) directly correlate with AI exposure risk, or are other factors at play?","Are the roles with higher AI exposure (E2) the same roles where women are underrepresented (E1)?"],"what_would_change_my_mind":["Evidence showing consistent AI-displacement risk patterns across multiple markets (beyond UK).","Data linking underrepresentation (E1) to specific roles with measurable AI exposure (E2).","Longitudinal studies demonstrating causal impact of AI on women''s tech employment."]}', 'independent', 1, '2026-02-09 18:50:40.286689');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (486, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Heretic', '{"questions":[{"to":"Orthodox","q":"Does [JL15-E1] provide any evidence that the underrepresentation of women in computing/tech roles is *caused* by systemic barriers (e.g., discrimination, structural exclusion) rather than other factors like career preferences or pipeline issues? If not, how can we conclude the claim is ''SUPPORTED'' without addressing causality?","evidence_refs":["E1"]},{"to":"Orthodox","q":"[JL15-E2] shows women in the UK are in roles with higher AI-displacement risk, but does this data control for *role type* (e.g., clerical vs. technical) or *industry sector*? Without such controls, how can we rule out that the risk is role-specific rather than gender-specific?","evidence_refs":["E2"]}]}', 'cross_exam', 3, '2026-02-09 18:50:52.031566');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (488, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Skeptic', '{"questions":[{"to":"Orthodox","q":"Does [JL15-E1] provide any breakdown of *which* computing/tech roles women are underrepresented in? If not, how can we confidently link underrepresentation to AI-displacement risk without knowing role-specific participation?","evidence_refs":["JL15-E1","JL15-E2"]},{"to":"Heretic","q":"If [JL15-E2] shows women are overrepresented in high-AI-risk roles in the UK, what alternative explanations (beyond correlation) could account for this pattern, and does the evidence rule out systemic factors?","evidence_refs":["JL15-E2"]}]}', 'cross_exam', 5, '2026-02-09 18:51:00.144966');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (495, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"Does [JL15-E2]''s UK data demonstrate that women''s higher AI-displacement exposure is attributable to gender (not role/industry confounds), or does it merely reflect their overrepresentation in roles with inherent displacement risk (e.g., clerical vs. technical)?","a":"[JL15-E2] shows women are more represented in roles with higher AI-displacement exposure, but the evidence does not isolate gender as the causal factor. Role/industry confounds remain unaddressed.","evidence_refs":["JL15-E2"],"admission":"uncertain"}]}', 'dispute', 2, '2026-02-09 18:51:20.910744');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (512, '1862a482-42b6-4422-bc1f-ca40fe5a5567', 'JL16', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"Does JL16-E1''s assertion that a ''blanket +50% net premium [is] not supported'' imply the evidence actively refutes the claim''s universality, or merely fails to validate it for *any* segment (leaving room for Orthodox''s localized exceptions)?","a":"JL16-E1 rejects a *universal* +50% net premium but does not actively refute *all* localized exceptions. It merely fails to validate the claim for any segment, leaving Orthodox''s position of ''INSUFFICIENT'' intact.","evidence_refs":["JL16-E1"],"admission":"insufficient"}]}', 'dispute', 2, '2026-02-09 18:57:19.218331');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (465, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Heretic', '{"proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"key_points":["Double-digit growth in 2024 may reflect a post-pandemic rebound rather than sustained outperformance vs. other formats, as no comparative data for e-books or print is provided in the evidence.","Digital audiobooks dominating revenue (~99%) suggests market saturation; further growth may be constrained by limited remaining non-digital segments to convert.","YoY growth metrics alone do not establish superiority—absolute revenue or market share trends for competing formats are absent, leaving the claim of ''outperformance'' unverified."],"uncertainties":["Whether the 13% growth rate is exceptional or merely average for the audiobook industry’s historical trends.","If other formats (e.g., e-books) experienced similar or higher growth rates in 2024, undermining the ''outperformance'' assertion."],"what_would_change_my_mind":["Direct comparison data showing audiobooks’ growth rate exceeded that of e-books, print, or other formats in 2024.","Evidence that the 13% growth was driven by new user adoption rather than existing user behavior shifts (e.g., subscription churn)."]}', 'independent', 1, '2026-02-09 18:46:02.576845');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (471, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Skeptic', '{"questions":[{"to":"Orthodox","q":"Does [EN17-E1]''s ~13% YoY growth inherently prove ''outperformance'' without comparative growth rates for e-books or print, or could it reflect broader industry trends?","evidence_refs":["E1"]},{"to":"Heretic","q":"If [EN17-E2] shows digital audiobooks at ~99% revenue, what specific evidence would confirm market saturation rather than continued expansion into untapped segments?","evidence_refs":["E2"]}]}', 'cross_exam', 5, '2026-02-09 18:46:22.166236');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (473, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Heretic', '{"answers":[{"q":"Does [EN17-E1]''s ~13% YoY growth inherently prove ''outperformance'' without comparative growth rates for e-books or print, or could it reflect broader industry trends?","a":"INSUFFICIENT evidence in pack. [EN17-E1] provides only audiobook growth data; no comparative metrics for e-books or print are included to validate ''outperformance'' or rule out industry-wide trends.","evidence_refs":["E1"],"admission":"insufficient"},{"q":"If [EN17-E2] shows digital audiobooks at ~99% revenue, what specific evidence would confirm market saturation rather than continued expansion into untapped segments?","a":"[EN17-E2] alone does not confirm saturation. However, ~99% digital revenue suggests minimal remaining non-digital segments to convert, implying growth may rely on existing user expansion (e.g., subscriptions) rather than new adoption. No data on untapped segments is provided.","evidence_refs":["E2"],"admission":"none"}]}', 'cross_exam', 7, '2026-02-09 18:46:30.412637');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (474, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Orthodox', '{"final_proposed_verdict":"SUPPORTED","evidence_used":["E1","E2"],"what_i_changed":["Clarified that ''outperformance'' is inferred from double-digit growth ([E2]) and sustained revenue increase ([E1]), not direct comparison to other formats (absent in evidence).","Acknowledged lack of comparative data as a limitation but maintained ''SUPPORTED'' due to strong growth signals."],"remaining_disagreements":["Whether 13% YoY growth ([E1]) constitutes ''outperformance'' without comparative data for other formats (e.g., e-books/print).","Whether ~99% digital dominance ([E2]) indicates expansion or saturation (no data on untapped segments)."],"confidence":0.85}', 'revision', 1, '2026-02-09 18:46:34.08248');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (475, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Heretic', '{"final_proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"what_i_changed":["Acknowledged Orthodox''s point that double-digit growth ([E2]) may signal relative strength, but maintained ''INSUFFICIENT'' due to lack of comparative data for other formats to validate ''outperformance''.","Clarified that ~99% digital dominance ([E2]) does not inherently prove saturation, but absence of untapped segment data leaves expansion claims unverified."],"remaining_disagreements":["Whether 13% YoY growth ([E1]) constitutes ''outperformance'' without comparative growth rates for e-books/print.","Whether ~99% digital revenue ([E2]) indicates saturation or untapped expansion potential."],"confidence":0.8}', 'revision', 2, '2026-02-09 18:46:37.493724');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (476, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Skeptic', '{"final_proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"what_i_changed":["Acknowledged Orthodox''s point that double-digit growth ([E2]) may signal relative strength, but maintained ''INSUFFICIENT'' due to lack of comparative data for other formats","Clarified that ~99% digital dominance ([E2]) does not inherently prove saturation, but still lacks evidence of untapped segments or unit sales"],"remaining_disagreements":["Orthodox infers ''outperformance'' from double-digit growth ([E2]) without direct comparative metrics for e-books/print","Orthodox interprets revenue growth ([E1]) as volume expansion, while I require unit sales data to rule out price inflation"],"confidence":0.85}', 'revision', 3, '2026-02-09 18:46:40.970139');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (489, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"Does [JL15-E1] provide any breakdown of *which* computing/tech roles women are underrepresented in? If not, how can we confidently link underrepresentation to AI-displacement risk without knowing role-specific participation?","a":"[JL15-E1] does not specify role-level breakdowns of underrepresentation. However, [JL15-E2] independently establishes women''s overrepresentation in high-AI-risk roles (UK), allowing linkage without requiring [JL15-E1] to detail roles.","evidence_refs":["JL15-E1","JL15-E2"],"admission":"none"}]}', 'cross_exam', 6, '2026-02-09 18:51:02.8529');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (496, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Heretic', '{"answers":[{"q":"Does [JL15-E2]''s UK data demonstrate that women''s higher AI-displacement exposure is attributable to gender (not role/industry confounds), or does it merely reflect their overrepresentation in roles with inherent displacement risk (e.g., clerical vs. technical)?","a":"[JL15-E2] does not demonstrate gender-attributable risk. The evidence lacks controls for role/industry confounds, leaving the cause ambiguous—women may be overrepresented in inherently high-risk roles, not disproportionately affected *because* of gender.","evidence_refs":["E2"],"admission":"insufficient"}]}', 'dispute', 3, '2026-02-09 18:51:23.677073');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (507, '1862a482-42b6-4422-bc1f-ca40fe5a5567', 'JL16', 'mistral/mistral-large-latest', 'Heretic', '{"answers":[{"q":"JL16-E1 states that a blanket +50% net premium is not supported. How do you reconcile this with your claim that localized premiums *might* exist, given the evidence does not provide granular data to validate such exceptions?","a":"JL16-E1 explicitly rejects a *blanket* +50% net premium, which aligns with the claim''s universal assertion. The evidence does not contradict the *possibility* of localized premiums but provides no data to validate them (INSUFFICIENT evidence in pack).","evidence_refs":["JL16-E1"],"admission":"insufficient"},{"q":"JL16-E2 notes freelancing growth but does not validate a universal premium. What specific missing evidence (e.g., role-level comparisons, cost breakdowns) would bridge the gap between growth trends and earnings claims?","a":"JL16-E2 confirms growth ≠ earnings validation. Missing evidence includes: (1) role-specific net earnings comparisons, (2) granular cost breakdowns (taxes, benefits, downtime), and (3) longitudinal data isolating high-demand niches.","evidence_refs":["JL16-E2"],"admission":"none"}]}', 'cross_exam', 7, '2026-02-09 18:57:01.873451');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (466, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Skeptic', '{"proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"key_points":["Double-digit growth in 2024 revenue (~13% YoY) is presented as ''strong growth'' [EN17-E1]","Digital audiobooks dominate (~99% of revenue), suggesting format consolidation [EN17-E2]"],"uncertainties":["No comparison data for other book formats (e.g., e-books, print) to validate ''outperformance'' claim","Revenue growth may reflect price increases, not unit sales or user adoption","Survey data lacks granularity on user demographics or market saturation risks"],"what_would_change_my_mind":["Direct comparative growth metrics for e-books/print in 2024","Unit sales data (not just revenue) to confirm volume growth","Breakdown of revenue drivers (e.g., subscription vs. à la carte purchases)"]}', 'independent', 1, '2026-02-09 18:46:02.609388');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (467, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Orthodox', '{"questions":[{"to":"Heretic","q":"Given [EN17-E1] reports a ~13% YoY growth in audiobook revenue, how do you reconcile your claim of ''post-pandemic rebound'' without comparative data for other formats (e.g., e-books or print) to substantiate that this growth is *not* outperformance?","evidence_refs":["E1"]},{"to":"Heretic","q":"[EN17-E2] states digital audiobooks constitute ~99% of revenue and returned to double-digit growth. What specific evidence (absent here) would demonstrate market saturation, rather than continued expansion, given the lack of data on non-digital segments'' size or conversion rates?","evidence_refs":["E2"]}]}', 'cross_exam', 1, '2026-02-09 18:46:06.08107');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (469, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Heretic', '{"questions":[{"to":"Orthodox","q":"E1 shows 13% YoY growth for audiobooks, but lacks comparative growth rates for e-books or print. How can you claim ''outperformance'' without evidence that audiobooks grew faster than other formats in 2024?","evidence_refs":["E1"]},{"to":"Orthodox","q":"E2 notes digital audiobooks dominate (~99% of revenue). If the market is nearly saturated, how do you reconcile this with your claim of ''expanding consumer adoption'' rather than just price inflation or subscription churn?","evidence_refs":["E2"]}]}', 'cross_exam', 3, '2026-02-09 18:46:14.025149');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (470, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"E1 shows 13% YoY growth for audiobooks, but lacks comparative growth rates for e-books or print. How can you claim ''outperformance'' without evidence that audiobooks grew faster than other formats in 2024?","a":"The claim of ''outperformance'' is based on the return to *double-digit growth* ([EN17-E2]), a threshold not consistently achieved by other formats in recent years. While direct comparative growth rates are absent, the sustained 13% YoY revenue increase ([EN17-E1]) in a mature market signals relative strength.","evidence_refs":["E1","E2"],"admission":"uncertain"},{"q":"E2 notes digital audiobooks dominate (~99% of revenue). If the market is nearly saturated, how do you reconcile this with your claim of ''expanding consumer adoption'' rather than just price inflation or subscription churn?","a":"The 13% YoY revenue growth ([EN17-E1]) and return to double-digit growth ([EN17-E2]) suggest *volume expansion*, not just price effects. While churn/subscription dynamics may contribute, the data implies net adoption growth, as revenue would stagnate without new users or increased consumption.","evidence_refs":["E1","E2"],"admission":"none"}]}', 'cross_exam', 4, '2026-02-09 18:46:19.222203');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (478, '0f2dda09-137b-4e6a-a1d6-34e27a8f568d', 'EN17', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"Does [E1]''s 13% YoY revenue growth for audiobooks exceed the 2024 YoY growth rates of e-books and print books, which would directly validate ''outperformance''?","a":"The evidence ([E1]) does not provide comparative YoY growth rates for e-books or print books, so direct validation of ''outperformance'' is insufficient.","evidence_refs":["E1"],"admission":"insufficient"}]}', 'dispute', 2, '2026-02-09 18:46:44.305019');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (481, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Orthodox', '{"proposed_verdict":"SUPPORTED","evidence_used":["JL15-E1","JL15-E2"],"key_points":["Persistent underrepresentation of women in computing/tech roles is documented in US statistics [JL15-E1], establishing a baseline disparity.","Women in the UK are disproportionately concentrated in tech roles with higher AI-displacement risk [JL15-E2], suggesting gendered exposure to automation threats."],"uncertainties":["Causality between underrepresentation and AI risk is not fully established in the evidence.","Geographic scope (US/UK) may limit generalizability to other markets."],"what_would_change_my_mind":["Evidence showing women are *not* overrepresented in high-AI-risk roles in other major markets.","Data demonstrating parity in tech participation across all measures [JL15-E1]."]}', 'independent', 1, '2026-02-09 18:50:40.230557');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (485, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Heretic', '{"answers":[{"q":"You argue [JL15-E1] lacks causal analysis of barriers, but the claim only requires establishing *underrepresentation* as a baseline. How does the absence of causal evidence in [JL15-E1] justify dismissing the documented disparity itself?","a":"[JL15-E1] confirms underrepresentation, but the *claim* links this to broader risks (e.g., AI displacement). Without causal analysis, we cannot assume underrepresentation *causes* disproportionate risk—it may correlate with other factors (e.g., role choices, education gaps).","evidence_refs":["E1"],"admission":"none"},{"q":"You suggest [JL15-E2]''s UK data may reflect role distribution rather than gender-specific risk. Does the evidence in [JL15-E2] provide *any* countervailing data showing women are equally represented in AI-resilient roles, or is this purely speculative?","a":"INSUFFICIENT evidence in pack. [JL15-E2] shows *higher* exposure in women-dominated roles but does not compare representation in AI-resilient roles. The speculation is grounded in the lack of evidence for gender-specific targeting.","evidence_refs":[],"admission":"insufficient"}]}', 'cross_exam', 2, '2026-02-09 18:50:48.347275');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (487, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Orthodox', '{"answers":[{"q":"Does [JL15-E1] provide any evidence that the underrepresentation of women in computing/tech roles is *caused* by systemic barriers (e.g., discrimination, structural exclusion) rather than other factors like career preferences or pipeline issues? If not, how can we conclude the claim is ''SUPPORTED'' without addressing causality?","a":"[JL15-E1] documents persistent underrepresentation but does not establish causality. The claim is SUPPORTED based on the *existence* of disparity, not its causes, as the evidence pack does not require causal proof for the stated claim.","evidence_refs":["JL15-E1"],"admission":"none"},{"q":"[JL15-E2] shows women in the UK are in roles with higher AI-displacement risk, but does this data control for *role type* (e.g., clerical vs. technical) or *industry sector*? Without such controls, how can we rule out that the risk is role-specific rather than gender-specific?","a":"[JL15-E2] suggests distributional risk without explicit controls for role/industry. However, the claim’s focus on *disproportionate* exposure to women aligns with the evidence’s framing, even if confounds exist.","evidence_refs":["JL15-E2"],"admission":"uncertain"}]}', 'cross_exam', 4, '2026-02-09 18:50:56.975665');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (493, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Skeptic', '{"final_proposed_verdict":"INSUFFICIENT","evidence_used":["E1","E2"],"what_i_changed":["Acknowledged Orthodox''s point that E1 establishes underrepresentation as a baseline, but maintained verdict due to unresolved gaps in linking E1 and E2 (role-specific vs. gender-specific risk).","Clarified that E2''s UK data alone cannot generalize to ''disproportionate'' risk without cross-market validation."],"remaining_disagreements":["Whether E2''s distributional risk (UK) is gender-specific or role-specific (e.g., clerical vs. technical).","Whether underrepresentation (E1) and AI exposure (E2) are causally linked or merely correlated."],"confidence":0.8}', 'revision', 3, '2026-02-09 18:51:16.284972');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (494, '7cf84490-10f1-4dd1-877d-08bed10a685f', 'JL15', 'mistral/mistral-large-latest', 'Skeptic', '{"questions":[{"q":"Does [JL15-E2]''s UK data demonstrate that women''s higher AI-displacement exposure is attributable to gender (not role/industry confounds), or does it merely reflect their overrepresentation in roles with inherent displacement risk (e.g., clerical vs. technical)?","evidence_refs":["JL15-E2"]}]}', 'dispute', 1, '2026-02-09 18:51:18.405182');
INSERT INTO public.run_messages (id, run_id, case_id, model_key, role, content, phase, round, created_at) VALUES (500, '1862a482-42b6-4422-bc1f-ca40fe5a5567', 'JL16', 'mistral/mistral-large-latest', 'Skeptic', '{"proposed_verdict":"REFUTED","evidence_used":["JL16-E1","JL16-E2"],"key_points":["Blanket +50% net premium claim is explicitly unsupported by [JL16-E1], which stresses accounting for unpaid time, taxes, and benefits.","[JL16-E2] confirms freelancing growth but does not validate a universal hourly premium net of costs, contradicting the claim''s universality."],"uncertainties":["Whether *specific* freelance roles or markets achieve a +50% premium (evidence only refutes blanket claims).","Degree to which unpaid time/benefits vary by region or skill level (not addressed in evidence)."],"what_would_change_my_mind":["Peer-reviewed data showing a consistent +50% net premium *after* adjusting for downtime, taxes, and benefits across multiple markets.","Evidence isolating high-demand freelance roles where premiums exceed costs universally."]}', 'independent', 1, '2026-02-09 18:56:28.753969');
